package net.faithgen.sdk.singletons;import android.graphics.Bitmap;import android.util.LruCache;import com.android.volley.RequestQueue;import com.android.volley.toolbox.ImageLoader;import com.android.volley.toolbox.Volley;import net.faithgen.sdk.SDK;public class VolleySingleton {    private static VolleySingleton ourInstance = new VolleySingleton();    private RequestQueue requestQueue;    private ImageLoader imageLoader;    public static VolleySingleton getInstance() {        return ourInstance;    }    private VolleySingleton() {        this.requestQueue = Volley.newRequestQueue(SDK.getContext());        this.imageLoader = new ImageLoader(this.requestQueue, new ImageLoader.ImageCache() {            private LruCache<String, Bitmap> lruCache = new LruCache<>((int) (Runtime.getRuntime().maxMemory() / 1024 / 8));            @Override            public Bitmap getBitmap(String s) {                return lruCache.get(s);            }            @Override            public void putBitmap(String s, Bitmap bitmap) {                lruCache.put(s, bitmap);            }        });    }    public RequestQueue getRequestQueue() {        return requestQueue;    }    public ImageLoader getImageLoader() {        return imageLoader;    }}